    def enable_Line_edit(self):
        # 檢查每一個checkBox，若被勾選，
        # 將對應的lineEdit設定成可以使用，否則設定成不能使用
        for i in range(len(self.checkBox_list)):
            if self.checkBox_list[i].isChecked():
                self.lineEdit_list[i].setEnabled(True)
            else:
                self.lineEdit_list[i].setEnabled(False)
    
    def checkout(self):
        price_list = [45,45,50,40,40,55]
        total = 0
        total_num = 0
        # 檢查每一個checkBox，若被勾選，
        # 將加上LINEedit中的數量乘以對應的價格，累加總額
        for i in range(len(self.checkBox_list)):
            if self.checkBox_list[i].isChecked():
                num = int( self.lineEdit_list[i].text() )
                total_num+= num
                total += num * price_list[i]
        
        # 判斷付款方式哪一個被勾選，決定顯示的付款方式
        if self.radioButton.isChecked():
            payment = "現金"
        else:
            payment = "LINE PAY"
        
        ice = {1: "正常冰", 2: "微冰", 3: "去冰", 4:"溫飲", 5:"熱飲"}
        ice_value = self.horizontalSlider.value()
        # 根據slider選擇的數值，找出溫度文字
        ice_select = ice[ice_value] 
        
        # 顯示付款方式
        result = f"{total_num}杯{ice_select}總共{payment}付款{total}元"
        self.label_10.setText(result)
    
    
        
