    def retranslateUi(self, MainWindow):
        _translate = QtCore.QCoreApplication.translate
        MainWindow.setWindowTitle(_translate("MainWindow", "MainWindow"))
        self.label_2.setText(_translate("MainWindow", "輸入股票代號"))
        self.pushButton.setText(_translate("MainWindow", "顯示K線圖"))
        # 連結自訂函數
        self.pushButton.clicked.connect(self.onclick)
        
    def onclick(self):
        # 取的使用者輸入的股票代號
        stock_id = self.lineEdit.text()
        try:
            df = dp(stock_id)
            # 替換成畫好的圖
            self.label.setPixmap(QtGui.QPixmap("stock_Kbar.png"))
        except:
            self.label_2.setText("沒有這個股票代號")
            self.label.setPixmap(QtGui.QPixmap("起始圖.png"))
            
        # 用df的shape資訊，讓tablewidget繪製相同大小的表格
        self.tableWidget.setColumnCount(df.shape[1])
        self.tableWidget.setRowCount(df.shape[0])
    
        _translate = QtCore.QCoreApplication.translate            
        count = 0
        for i in df.index:
            # 建構列的物件
            item = QtWidgets.QTableWidgetItem()
            self.tableWidget.setVerticalHeaderItem(count, item)
            
            # 設定列的名稱
            item = self.tableWidget.verticalHeaderItem(count)
            item.setText(_translate("MainWindow", str(i)))
            count+= 1
        
        count = 0
        for c in df.columns:
            # 建構欄的物件
            item = QtWidgets.QTableWidgetItem()
            self.tableWidget.setHorizontalHeaderItem(count, item)
            
            # 設定欄的名稱
            item = self.tableWidget.horizontalHeaderItem(count)
            item.setText(_translate("MainWindow", c))
            count+= 1
        
        index_count = 0
        for index, row in df.iterrows():
            columns_count = 0
            
            for c in df.columns:
                # 建構儲存格的物件
                item = QtWidgets.QTableWidgetItem()
                self.tableWidget.setItem(index_count, columns_count, item)
                
                # 設定儲存格的內容
                item = self.tableWidget.item(index_count, columns_count)
                text = str( df.at[index, c] )
                item.setText(_translate("MainWindow", text))
                columns_count+= 1
            index_count+= 1
            
        
